for item in _input.all():
  parts = item.json.content.split(' | ')

  for part in parts:
    key, value = part.split(': ', 1)
    item.json[key.lower()] = value

    jobId = item.json.link.split('/')[-1].split('-')[-1]
    item.json["jobId"] = jobId
return _input.all()

