=export default async ({ page }) => {
  await page.goto("https://www.linkedin.com/jobs-guest/jobs/api/jobPosting/{{ $json.jobId }}")

  const items = await page.evaluate(() => {
    const elements = [...document.querySelectorAll('section.description>div>div>section>div')];
    const results = elements.map((el) => el.innerHTML.trim());
    return JSON.stringify(results);
  });

  return JSON.parse(items);
};

